float N21(vec2 p){
    p = fract(p*vec2(244.123, 231.532));
    p += dot(p, p+464.213);
    return fract(p.x*p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 5.;

    vec3 col = vec3(0);
    float n = N21(uv);
    float a = sin(uv.x+uv.y*.1)*.4+sin(uv.y-iTime);

    //nope
    //vec3 sand = smoothstep(-5., 2.+sin(uv.x)*.4+sin(uv.y-iTime), -uv.y)+vec3(.9,.5,.5);
    //col += sand;


    float minD = 100.;
    for(float i=0.; i<1.; i+=1./50.){
        vec2 n = vec2(N21(vec2(i*61.518,i)), N21(vec2(i, i*181.76)))*5.-2.5;
        n.y -= 2.;
        n.y *= .3;
        n.y += 1.+a+sin(iTime);
        n.x *= 2.;
        float d = length(n-uv);
        if(d<minD){
            minD = d;
        }

    }
    vec3 wave = smoothstep(-2., 2.+a, uv.y)+vec3(.2, .4, .8);
    wave += smoothstep(1.9+a, 2.2+a, uv.y)*.1;
    col += wave-.1;

    if(col.r>.9){
        col *= vec3(2., .5, .1)*(sin(uv.y*.3)+2.)*.5;
        col -= N21(floor(uv*30.))*.2; // sand texture
    }

    //col += minD;
    //wave += ;
    col += minD*.1;
    //col += N21(uv);

    fragColor = vec4(col, 1.);
}
