vec2 centre;

bool inCircle(vec2 p, vec2 centre, float radius) {
    vec2 d = p - centre;
    float ll = d.x*d.x + d.y*d.y;
return ll < radius*radius;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = 2.0*fragCoord/iResolution.xy - 1.0;
    uv.y *= iResolution.y/iResolution.x;

    // Output to screen
    if (inCircle(uv, vec2(0.5, 0.2), 0.25)) {
        fragColor = vec4(1, 0, 0,1.0);
    } else if (inCircle(uv, vec2(0.2, -0.3), 0.125)) {
        fragColor = vec4(0, 1, 1,0.5);
    } else {
        fragColor = texture(iChannel0, 0.5*(uv+1.0)*0.01*iTime);
    }
}
