#define R iResolution.xy
#define Main void mainImage(out vec4 Q, in vec2 U) 
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U)/R)
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
vec4 hash(vec4 p4)
{
    p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}
vec2 normal(vec2 u){
    if (length(u)==0.) return vec2(0);
    else return normalize(u);
}

Main {

    Q = A(U);
    float T = atan(Q.y,Q.x);
    vec4 dQ = vec4(0);
    for (float x = -2.; x<=2.; x++) 
    for (float y = -2.; y<=2.; y++) if (x!=0.||y!=0.) {
        vec2 u = vec2(x,y);
        vec4 a = A(U+u);
        float t = atan(a.y,a.x);
        dQ += vec4(cos(t),sin(t),0,1)*exp(-100.*(.7+length(U-.5*R)/R.y)*abs(sin(6.*t)-sin(6.*T)))/length(u);


    }
    if (dQ.w>0.){
    Q.xyz = dQ.xyz/dQ.w;
    }
    if (iFrame < 1) {
        vec4 H = hash(vec4(U,iFrame,iTime));
        Q.xy = normalize(H.xy*2.-1.);
        Q.w = 1.;

    }


}

Main {
    vec4 n = B(U+vec2(0,1));
    vec4 e = B(U+vec2(1,0));
    vec4 s = B(U-vec2(0,1));
    vec4 w = B(U-vec2(1,0));
    vec2 g = vec2(
        length(e.x)-length(w.x),
        length(n.x)-length(s.x)+1.
    );
    g = clamp(g,-.5,.5);
    vec4 q = A(U);
    q = .2+.9*sin(2.3+.5*vec4(1,2,3,4)*atan(q.x,q.y));
    Q = mix(q,B(U+.5*g*sign(Q.x-.5)),.98-.015*(1.-length(U-.5*R)/R.y));
    Q = clamp(Q,0.,1.);


}
