#define EDGE_BLUR 0.03

int  u_EntityCount = 3;
vec3 u_Entity[3] = vec3[3](
    vec3(0.33, 0.2, 0.15),// x, y, radius
    vec3(0.73, 0.4, 0.23),
    vec3(0.23, 0.1, 0.13)
);

float fog(vec2 pos) {


    float color = 1.0;
    for (int i = 0; i < u_EntityCount; i++) {
        vec3 entity = u_Entity[i];
        float r = entity.z;
        float dist = length(pos - entity.xy);
        float inCircle = smoothstep(r-EDGE_BLUR, r, dist);
        color *= inCircle;
    }
    return 1.0-color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/min(iResolution.x, iResolution.y);
    vec4 color = texture(iChannel0, uv);

    float ambient = 0.2;// 环境光亮度
    float pointLight = fog(uv);// 点光源亮度
    float light = clamp(pointLight + ambient, 0.0, 1.0);

    fragColor = color * light;
}
