#define side(a) dot(uv, vec2(sin(a), cos(a)))
#define NUM_SEGS 30
#define NUM_LINES 20.
#define NUM_LINES2 20.
#define pi acos(-1.0)

vec2 RotateUV(vec2 uv, float rotation) {
    return vec2(cos(rotation) * uv.x + sin(rotation) * uv.y, cos(rotation) * uv.y - sin(rotation) * uv.x); }

float sdPie(vec2 p, vec2 c, float r ) {
    float rotation1 = pi/2.;
	p = RotateUV(p, rotation1);
    p.x = abs(p.x);
    float l = length(p) - r;
	float m = length(p - c);
    return max(l,m*sign(c.y*p.x-c.x*p.y)); }

float sdPietime(vec2 p, vec2 c, float r ) {
    float rotation1 = (pi/2.)*sin(iTime)*0.01+1.55;
	p = RotateUV(p, rotation1);
    p.x = abs(p.x);
    float l = length(p) - r;
	float m = length(p - c);
    return max(l,m*sign(c.y*p.x-c.x*p.y)); }

float sdPie2(vec2 p, vec2 c, float r ) {
    float rotation2 = pi/0.665;
	p = RotateUV(p, rotation2);
    p.x = abs(p.x);
    float l = length(p) - r;
	float m = length(p - c);
    return max(l,m*sign(c.y*p.x-c.x*p.y)); }

float sdPie2time(vec2 p, vec2 c, float r ) {
    float rotation2 = (pi/0.665)*0.3*sin((iTime*0.5+3.22)*sin(iTime*0.02))*0.02-1.55;
	p = RotateUV(p, rotation2);
    p.x = abs(p.x);
    float l = length(p) - r;
	float m = length(p - c);
    return max(l,m*sign(c.y*p.x-c.x*p.y)); }

float triangle1(vec2 uv, float size) {
    float timer = sin(iTime*0.3)*sin(iTime*0.3)*0.4;
    float rotation1 = pi+0.3+timer;    
	uv = RotateUV(uv, rotation1);
    uv.x = abs(uv.x);
    float w = fwidth(uv.y);
    float a = pi/3.;
    float d1 = side(a);
    a = pi*5./5.;
    float d2 = side(a); 
    float d = max(d1, d2);
    return smoothstep(w, -w, d-size); }

float triangle2(vec2 uv, float size) {
    float timer = cos(iTime*0.3+2.5)*cos(iTime*0.3+2.5)*0.4;
    float rotation1 = pi*2.+0.3+timer;    
	uv = RotateUV(uv, rotation1);
    uv.x = abs(uv.x);
    float w = fwidth(uv.y);
    float a = pi/3.;
    float d1 = side(a);
    a = pi*5./5.;
    float d2 = side(a); 
    float d = max(d1, d2);
    return smoothstep(w, -w, d-size); }

float Circle(vec2 p, vec2 c) {
    float d = length(p-c);
    return smoothstep(0.002, 0.0, d - 0.004); }

float sdCircle( in vec2 p, in float r )  {
    return length(p)-r; }

float Line(vec2 p, vec2 a, vec2 b) {
	vec2 pa = p - a, ba = b - a;
    float t = clamp(dot(pa, ba)/dot(ba, ba), 0.0, 1.0);
    vec2 c = a+ba*t;
    float d = length(c-p);
    float l = 0.002, g = 0.002;
	return min(1.0, 0.001/d); }

