vec2 Bezier(vec2 a, vec2 b, vec2 c, float t) {
    return mix(mix(a, c, t), mix(c, b, t), t); }

vec2 polarcords (float x, float p) {
    float u = fract(sin(x))*2.0*3.14159265;
    return vec2(sin(u), cos(u)) * p; }

float getSquare(vec2 p, vec2 rp) {   
    p += rp;
    vec2 bl = step(abs(p * 1.0 - 0.5), vec2(0.1));
    float rt = bl.y * bl.x;
	return rt; }

