vec3 Dbet(vec2 uv) {
    vec3 col = vec3(0.0);
    col += getSquare(uv, vec2(uv.x+0.0, uv.y+0.0));
    col += getSquare(uv, vec2(uv.x-0.2, uv.y+0.0));
    col += getSquare(uv, vec2(uv.x-0.4, uv.y+0.0));
