#define ZOOM  20.
#define SPEED 5.

void mainImage(out vec4 O, vec2 U) {
  vec2 R = iResolution.xy;
  U = (U * 2. - R) / R.y * ZOOM;

  float dst = length(U);

  vec3 col = vec3(smoothstep(.0, .2, cos(dst + atan(U.x, U.y) - iTime * SPEED)));
  float shadow = smoothstep(.0, 3., dst / ZOOM);

  O = vec4(col * shadow, 1);
}
