void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*iResolution.xy-fragCoord)/iResolution.y;
    vec2 mv = (2.*iResolution.xy-iMouse.xy)/iResolution.y;
    float t= iTime*.5;
    float scale = 1.;
    float d = scale;
    float shift = -1.9;
    float delta = 2.*sin(.4*t);
    vec2 dx = vec2(0.02);
    float iters = 5.;

    for (float i=0.;i<iters;i++){
        uv += d*tan(mod(uv+i*dx,pow(.5,i))+shift);
        shift += delta;
        if(i<iters)d = mod(length(uv),scale);
    }
    d = mod(d,1.);
    vec3 col = vec3(d);    
    fragColor=vec4(col,1.);
}
