float remap(float oldmin,float oldmax, float newmin,float newmax,float x){
            float remap =((x-oldmin)*(newmax-newmin))/oldmax-oldmin;
            remap+=oldmin;
            return remap;

    }
vec3 Color_remap(vec3 oldmin,vec3 oldmax, vec3 newmin,vec3 newmax,float x){
            vec3 v_x=vec3(x);
            vec3 Color_remap =((v_x-oldmin)*(newmax-newmin))/oldmax-oldmin;
            Color_remap+=oldmin;
            return Color_remap;

    }



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    // Output to screen


    vec2 mouse = vec2(iMouse)/vec2(iResolution);
    uv -=mouse;

    float v  =atan(uv.x,uv.y)/3.14159;
    v+=iTime*0.1;
    v =fract(v*3.);
    float cir=distance(uv,vec2(0.0));
    cir =fract(cir+iTime*0.05);
    cir+=sin(cir);
   // float thr =0.7;
   // float r=smoothstep(0.25+thr,0.75-thr,cir);
    float wave_amount =0.2;
    cir=mod(cir,wave_amount);
    cir =remap(0.,wave_amount,0.,1.0,cir);

    vec3 col1=vec3(0.5,0.0,1.0);
    vec3 col2=vec3(0.8,0.5,0.2);
    vec3 col_mix=mix(col1,col2,v);

    vec3 col3=vec3(0.0,1.0,0.45);
    vec3 col4=vec3(0.2,0.4,0.3);
    vec3 col_mix_1=mix(col1,col2,cir);
    //vec3 col2  =vec3(1.0,0.6,1.2)*v*cir;




    fragColor = vec4 (max(col_mix,col_mix_1), 1.0);
}
