vec2 uv_map(vec2 uv, float strength){
  float from_center=abs(uv.x-0.5);
  return vec2(uv.x,uv.y-log(1.+uv.y)*pow(from_center,1.5)*strength);
}


float dist_from_int(float x){
  return abs(x-round(x));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;


    uv=uv_map(uv,1.2+1.2*cos(iTime));

    // Time varying pixel color
    vec3 col = texture( iChannel0, uv ).xyz;
    float grid_factor=min(dist_from_int(20.*uv.x),dist_from_int(20.*uv.y));

     col*=grid_factor<0.075?15.*grid_factor:1.;

    // Output to screen
    fragColor =vec4(col,1.);
}
