void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 color = texture(iChannel0, uv);
    vec4 color2 = texture(iChannel1, uv);//texture(iChannel0, uv-0.001);

    vec3 invr = vec3(1, 1, 1) - color.rgb;  // invert
    color.rgb = mix(color.rgb, invr, 0.35); // apply invert to ichannel0

    vec4 finalColor = mix(color, color2, -0.425);  // mix ichannel0 and ichannel1 together | mess with the value

    //if u use the same channel and add time offset to the second color it would give a better effect
    // to see the effect better quickly pause then unpause iChannel1

	fragColor = finalColor;
}
