void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e; vec3 p,q=vec3(1,2,1); vec2 h;
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e);
    p.z-=2.;
    p*=rotate3D(iTime,q);

    for(j=0.;j++<4.;){
        p=abs(abs(p)-.04)-.02;
        p.xy*=rotate2D(24.);
        h=abs(vec2(length(p.xz),p.y))-.07;
        d=min(max(h.x,h.y),-.05)+length(max(h,-.1));
    }    
    e+=d;

}o+=vec4(exp(p),1)*3./i;
}
