// Sample Buffer A (terrain) and Buffer B (god rays/shadow/ambient occlusion),
// then composite with atmosphere from Common and apply color grading.

// Disable these four to view only the atmosphere function.
#define TERRAIN
#define STARS
#define CELESTIAL_BODIES
#define LENS_FLARE
#define MOON_LIGHT
//#define HEIGHT_FOG // Enable this to showcase the fog fade feature

#define AUTO_EXPOSURE
#define TONEMAP
#define GAMMA
#define DITHER

// https://www.shadertoy.com/view/XlXcW4
vec3 hash(uvec3 x)
{
    const uint k = 1103515245U;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;    
    return vec3(x)*(1.0/float(0xffffffffU));
}

void GetCamera(vec2 uv, out vec3 ro, out vec3 rd)
{
    ro = vec3(0, 3000, 0);
    float aspect = iResolution.x / iResolution.y;
    rd = normalize(vec3((uv - 0.5) * vec2(aspect, 1.0), 1.0));
}
vec3 ExpandPackedNormal(vec2 normalXZ)
{
    vec3 n = vec3(normalXZ.x, 0, normalXZ.y);
    n.y = sqrt(1.0 - clamp(dot(n.xz, n.xz), 0.0, 1.0));
    return n;
}

vec3 TonemapACES(vec3 color)
{
    const float a = 2.51;
    const float b = 0.03;
    const float c = 2.43;
    const float d = 0.59;
    const float e = 0.14;
    return (color * (a * color + b)) / (color * (c * color + d) + e);
}

#define BLADE_COUNT1    4.0
#define BLADE_COUNT2    8.0
#define BLADE_COUNT3    16.0
#define THICKNESS       2.0 // Controls the thickness of the limbs. Values below 2 will cause aliasing.
#define GLARE_INTENSITY 0.9 // Strength of rainbow circle glare.
#define GLARE_WIDTH     0.1 // Size of rainbow circle glare.
#define GLOW_INTENSITY  0.2 // Intensity of rainbow circle glare.
#define FACE_DIRECTION      // Rotate the flare to face the input direction

float saturate(float x) { return clamp(x, 0.0, 1.0); }
float remap (float x, float a, float b, float c, float d) { return saturate(c + (((x - a) / (b - a)) * (d - c))); }
float remap01 (float x, float a, float b) { return saturate((x - a) / (b - a)); }

// https://www.shadertoy.com/view/4djSRW
float hash11(float p)
{
    p = fract((p+1.0)*0.1031);
    p *= p+33.33;
    return fract(p*p*2.0);
}
// https://www.shadertoy.com/view/XljGzV
vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
// https://www.shadertoy.com/view/Xc2XR1
float GetFlareBlades(float x, float y, float count, float size, float roll, float offset, float k)
{
    float x0 = x;
    x = fract(x + 1.0 / count);
    y = remap01(y, 0.0, size);
    float s = x * count;
    float hash = hash11(float(int(s) % int(count / 2.0)) + offset);
    float blade = abs(fract(s + 0.5) - 0.5) * 2.0;
    float aa = mix(fwidth(x * count), fwidth(fract(x + 0.5) * count), step(fract(x + 0.25), 0.5));
    blade = remap01(blade, 1.0 - aa * THICKNESS, 1.0);
    float l = cos(hash * 2.0 * PI + roll * 4.0) * 0.4 + 0.6;
    return blade * remap01(y, l * 0.5, 0.0);
}

vec3 GetFlare(vec2 uv, vec2 toCenter, float visibility)
{
    float roll = atan(-toCenter.y, toCenter.x);

    vec2 dir = (uv - 0.5) * 2.0;
    float y = length(dir);
    dir /= y;
    float theta = atan(dir.y, dir.x);
#ifdef FACE_DIRECTION
    theta += roll;
#endif
    float x = theta / (2.0 * PI) + 0.125;
    float flare = 
        GetFlareBlades(x + 0.000, y, BLADE_COUNT1, visibility * 1.0, roll, 0.0,   40.0) * 0.4 +
        GetFlareBlades(x + 0.125, y, BLADE_COUNT2, visibility * 0.6, roll, 10.0,  20.0) * 0.1 +
        GetFlareBlades(x + 0.125, y, BLADE_COUNT3, visibility * 0.4, roll, 100.0, 2.0)  * 0.05;

    float glare = pow(saturate(1.0 - y), 4.0 + (1.0 - visibility) * 4.0) * 0.1;
    vec3 n = vec3((uv - vec2(0.5)) * 2.0, 0.0);
    n = normalize(vec3(n.xy, sqrt(1.0 - saturate(dot(n.xy, n.xy)))));
    float glareSize = length(toCenter);
    float glareWidth = GLARE_WIDTH;
    float glareY = y - glareSize;
    glare = saturate(dot(vec3(-toCenter, 0.5), n)) * smoothstep(glareWidth, 0.0, abs(glareY));
    glare *= 0.5 + cos(theta * floor(500.0 * glareSize)) * 0.5;
    float glareColorKey = (glareY / glareWidth + 0.5) * 0.5;
    float r = (1.0 - glareColorKey);
    float g = 1.0 - abs(glareColorKey - 0.5) * 2.0;
    float b = glareColorKey;
    vec3 glareColor = vec3(r, g, b) * glareSize * GLARE_INTENSITY;

    float glow = saturate(1.0 - y);
    glow = glow*glow*GLOW_INTENSITY;

    return (flare + glow) / (y + 1e-5) + glare * glareColor;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 mouse = iMouse / iResolution.xyxy;
    float time = -iTime * 0.2 + 3.5;
    vec2 sunPos = 0.5 + vec2(cos(time) * 0.4, sin(time) * 0.45);
    if (iMouse.x > 10.0)
    {
        sunPos = mouse.xy;
    }
    vec3 sunDir;
    vec3 foo;
    GetCamera(sunPos, foo, sunDir);

    vec3 sunRadiance = vec3(1, 1, 1);

    const vec3 moonDir = normalize(vec3(0.5, 0.25, 1));
    const vec3 moonCenter = moonDir * 384400e3;
    const float moonRadius = 1737e3 * SUN_DISC_SIZE * 0.9; // Scale moon size with sun size
    vec3 moonRadiance = vec3(0.01) * (dot(sunDir, -moonDir) * 0.5 + 0.5);

    sunRadiance *= mix(1.0, smoothstep(1.0, 0.99985, dot(sunDir, moonDir)), 0.999); // Eclipse

    vec2 uv = fragCoord / iResolution.xy;

    float dither = textureLod(iChannel2, uv * iResolution.xy / vec2(1024), 0.0).x;
    dither = fract(dither + (0.61803398875 * float(iFrame & 255)));

    vec3 ro, rd;
    GetCamera(uv, ro, rd);

    float rl = INFINITY;

    vec3 color = vec3(0.0);

    float atmosphereOcclusion = 1.0;

#ifdef TERRAIN
    vec4 occlusionTex = texture(iChannel1, uv);
    atmosphereOcclusion = occlusionTex.x;
    float shadow = pow8(occlusionTex.y);
    float ambientOcclusion = pow4(occlusionTex.z);

    // Sample terrain screen buffer
    vec4 terrain = texture(iChannel0, uv);
    if (terrain.x > 0.0)
    {
        // Terrain shading below

        rl = terrain.x;
        vec3 p = ro + rd * rl;

        vec3 n = ExpandPackedNormal(terrain.yz);

        vec4 terrain1 = texture(iChannel0, uv + vec2(1, 0) / iResolution.xy);
        vec4 terrain2 = texture(iChannel0, uv + vec2(0, 1) / iResolution.xy);
        vec3 n1 = ExpandPackedNormal(terrain1.yz);
        vec3 n2 = ExpandPackedNormal(terrain2.yz);
        float edge = 1.0 - pow(min(dot(n, n1), dot(n, n2)) * 0.5 + 0.5, 50.0);

        vec3 albedo = vec3(0.2, 0.22, 0.25); // Cliffs
        albedo = mix(albedo, vec3(0.55, 0.54, 0.53), edge); // Edge wear
        albedo = mix(albedo, vec3(0.95), smoothstep(0.7, 0.75, n.y)); // Snow

        // Color light by transmittance
        vec3 lt = GetLightTransmittance(p, sunDir, 1.0);

        // Direct
        float ndotl = dot(n, sunDir);
        color += albedo * clamp(ndotl, 0.0, 1.0) * sunRadiance * lt * shadow / PI;
    #ifdef MOON_LIGHT
        lt = GetLightTransmittance(p, moonDir, 1.0);
        ndotl = dot(n, moonDir);
        color += albedo * clamp(ndotl, 0.0, 1.0) * moonRadiance * lt / PI;
    #endif

        // Ambient
        vec3 an = mix(vec3(0, 1, 0), n, 0.25); // Biased ambient sample dir
        vec3 al = GetAtmosphere(p, an, INFINITY, sunDir, sunRadiance);
    #ifdef MOON_LIGHT
        al += GetAtmosphere(ro, rd, rl, moonDir, moonRadiance);
    #endif
        color += albedo * al * ambientOcclusion;

        // Specular
        vec3 r = reflect(rd, (n + n1 + n2) / 3.0); // Average normal for some slight specular AA
        vec3 rl = GetAtmosphere(p, r, INFINITY, sunDir, sunRadiance);
    #ifdef MOON_LIGHT
        rl += GetAtmosphere(p, r, INFINITY, moonDir, moonRadiance);
    #endif
        float fresnel = pow(1.0 - clamp(dot(rd, -n), 0.0, 1.0), 5.0);
        color += rl * (0.05 + fresnel * 0.95) * ambientOcclusion / PI;

        //fragColor = vec4(albedo, 1.0); return; // Debug albedo
    }
#else
    float occlusion = 1.0;
#endif

    // Get atmosphere (sun)
    vec4 transmittance;
#ifdef HEIGHT_FOG
    float altitude = max(0.0, (ro + rd * rl).y);
    float fogFactor = (1.0 - exp(-rl * 1e-4)) * exp(-sq(altitude) * 5e-7);
#else
    float fogFactor = 0.0;
#endif
    vec3 scattering = GetAtmosphere(ro, rd, rl, sunDir, sunRadiance, transmittance, fogFactor) * atmosphereOcclusion;

#ifdef TERRAIN
    transmittance.w *= terrain.x < 0.0 ? 1.0 : 0.0;
#endif

#ifdef STARS
    // Stars
    vec2 starCellF = fragCoord / 4.0;
    uvec2 starCell = uvec2(floor(starCellF));
    starCellF = fract(starCellF);
    vec3 starHash = hash(uvec3(starCell, 0));
    vec3 stars = smoothstep(0.1, 0.0, length(starCellF - starHash.xy)) * vec3(pow(starHash.z, 10.0)) * 1e-2;
    color += stars * transmittance.w;
#endif

#ifdef CELESTIAL_BODIES
    // Sun celestial body
    color += GetSunDisc(rd, sunDir) * sunRadiance * 1000.0 * transmittance.w;

    // Moon celestial body
    vec2 moonT = SphereIntersection(ro, rd, moonCenter, moonRadius);
    if (moonT.x > 0.0)
    {
        vec3 moonNormal = normalize(ro + rd * moonT.x - moonCenter);
        color = clamp(dot(moonNormal, sunDir), 0.0, 1.0) * sunRadiance / PI;
    }
#endif

    // Apply atmosphere (sun)
    color = color * transmittance.xyz + scattering;

#ifdef LENS_FLARE
    vec4 flareTransmittance;
    GetAtmosphere(ro, sunDir, INFINITY, sunDir, sunRadiance, flareTransmittance);
    #ifdef TERRAIN
    flareTransmittance.w *= step(texture(iChannel0, sunPos).x, 0.0);
    #endif
    #ifdef CELESTIAL_BODIES
    flareTransmittance.w *= step(SphereIntersection(ro, sunDir, moonCenter, moonRadius).x, 0.0);
    #endif
    vec2 flareUV = uv - sunPos + vec2(0.5);
    float aspect = iResolution.x / iResolution.y;
    flareUV.x = (flareUV.x - 0.5) * aspect + 0.5;
    flareUV = (flareUV - vec2(0.5)) * 1.0 + vec2(0.5);
    vec2 toCenter = vec2(0.5) - sunPos;
    color += GetFlare(flareUV, toCenter, 1.0) * flareTransmittance.xyz * flareTransmittance.w;
#endif

#ifdef MOON_LIGHT
    // Apply atmosphere (moon)
    color += GetAtmosphere(ro, rd, rl, moonDir, moonRadiance);
#endif

#ifdef AUTO_EXPOSURE
    // Auto-exposure
    vec3 radiance = GetAtmosphere(ro, vec3(0, 1, 0), INFINITY, sunDir, sunRadiance) +
        GetAtmosphere(ro, vec3(0, 1, 0), INFINITY, moonDir, moonRadiance);
    float lum = dot(radiance, vec3(0.3, 0.59, 0.11));
    color *= mix(1.0, 0.1 / lum, 0.1);
#endif

#ifdef TONEMAP
    color = TonemapACES(color);
#endif

#ifdef GAMMA
    color = pow(color, vec3(1.0 / 2.2));
#endif

#ifdef DITHER
    color += dither / 255.0 * 8.0;
#endif

    fragColor = vec4(color, 1.0);
}

