void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    int par = int(fragCoord.x+fragCoord.y)%2;  
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 c = texture(iChannel0,uv);

    if(par==0){
        fragColor = vec4(.299*c.r,vec3(0.));    
    }
    else
    if(par==1){
        float luma = (.299*c.r + .587*c.g + .114*c.b); 
        fragColor = vec4(vec3(luma),0.);
    }    

}
