void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   // uniforms
   float arcangle = iTime;
   float size = 0.5;
   vec2 centrevec = vec2(sin(arcangle*0.5),cos(arcangle*0.5));
   vec2 endvec = vec2(sin(arcangle),cos(arcangle));

   vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
   float d = 0.0;
   float pl = length(p);
   if(dot(centrevec, p) < centrevec.y*pl) 
   {
     d =  min(distance(p,vec2(0,1.0)*size),distance(p,endvec*size));
   }
   else
   {
     d = abs(size - pl);
   }

   float ds = 1.0-smoothstep(0.07,0.075,d);
   fragColor = vec4(ds*0.8,ds*0.8,0.0,1.0);
}
