void mainImage( out vec4 fragColor, in vec2 uv )
{
    float scale=1.;
    uv.x = scale*((uv.x-0.5*iResolution.x)/iResolution.y);
    uv.y = scale*(uv.y-0.5*iResolution.y)/iResolution.y;

    vec3 col = vec3(0.);

    float e=pow(2.,-0.0001*floor(float(iFrame)));
    float r=-5.;
    for(float s=-100.;s<=100.;s+=e){

    float a = pow(uv.x+s*sin(1./sin(uv.x)),2.)+pow(uv.y+s*sin(1./sin(uv.y)),2.);
    float b = pow(uv.x+s*cos(1./cos(uv.x)),2.)+pow(uv.y+s*cos(1./cos(uv.y)),2.);
    float c = pow(uv.x+s*sin(1./cos(uv.x)),2.)+pow(uv.y+s*sin(1./cos(uv.y)),2.);
    float d = pow(uv.x+s*cos(1./sin(uv.x)),2.)+pow(uv.y+s*cos(1./sin(uv.y)),2.);

    col+=0.25*e*vec3(pow(2.,r*abs(a-1.)),pow(2.,r*abs(a-2.)),pow(2.,r*abs(a)));
    col+=0.25*e*vec3(pow(2.,r*abs(b-1.)),pow(2.,r*abs(b-2.)),pow(2.,r*abs(b)));
    col+=0.25*e*vec3(pow(2.,r*abs(c-1.)),pow(2.,r*abs(c-2.)),pow(2.,r*abs(c)));
    col+=0.25*e*vec3(pow(2.,r*abs(d-1.)),pow(2.,r*abs(d-2.)),pow(2.,r*abs(d)));

   }
    fragColor = vec4(col,1.0);
}
