    float amplitude = 0.2;
    float frequency = 10.0;
    // SDF for a sine wave
    float sineSDF(vec2 p, float phase) {
        float minDist = 1e10; // Initialize with a large value
        for (float x = -1.0; x <= 1.0; x += 0.15) { // Sample along x-axis
            float y = amplitude * sin(frequency * x + phase);
            vec2 sinePoint = vec2(x, y);
            float dist = length(p - sinePoint);
            if (dist < minDist) {
                minDist = dist;
            }
        }
        return minDist;
    }
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Define the sine wave parameters

    float phase = iTime;


    // Calculate the distance to the sine wave
    float d = sineSDF(uv, phase);

    // Glow effect
    vec3 color = vec3(0.0, 0.5, 1.0) / (d * d * 100.0 + 0.50 + 0.1* sin(iTime));

    fragColor = vec4(color, 1.0);
}
