#define PI 3.1452654
#define PI2 (PI * 2.0)

vec4 texchange(vec2 uv) {
    uv.x = -0.02 + mod(uv.x, 0.1) / 6.0 + 0.94 * mod(floor(iTime * 6.0), 6.0) / 6.0 ;
    return texture(iChannel0, uv);
}

void mainImage( out vec4 f, in vec2 p )
{
    vec2 uv = p / iResolution.xy;
    float prop = iResolution.x / iResolution.y;
    vec2 pos = uv - 0.5;
    pos.y /= prop;

    p = vec2(-0.199,.809) + pos*pow(5.0, 3.0 * cos(.15 * iTime) - 2.0);

    float n = 0.;

    vec2 z = vec2(0.);

    for(n=0.;n<128. ; n++ ){
        z = vec2( z.x*z.x - z.y*z.y, 2.*z.x*z.y ) + p;
        float r = dot(z,z);
        if (r > 1e4) {
            vec2 fuv = vec2((atan(z.y, z.x) / PI2 - iTime), -0.1 + log(log(r)));
            f = vec4(vec3(0.2,0.2,0.3)*smoothstep(0.1,.3,r),1.0);
            return;
        }  
    }
    f= texchange((z * 10.0 + (-2.0 * iTime)) + 0.5);
}
