void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float factor = iMouse.x/iResolution.x;

    vec2 uv = fragCoord / iResolution.xy;
    vec4 img = texture(iChannel0,uv);
    float lum = (img.r + img.g + img.b)/3.;
    vec3 dist = img.rgb-vec3(lum);

    img.rgb -= dist.rgb*factor;

    fragColor.rgb = vec3(img);
}
