void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Set up UV coordinates
    vec2 uv = (fragCoord.xy / iResolution.xy) * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Mirror UVs for symmetry
    uv = 1.0 - abs(uv);

    // Calculate polar coordinates
    float angle = atan(uv.y, uv.x);
    float radius = length(uv);

    // Adjust animation speed
    float slowTime = iTime * 0.1;

    // Animate radius
    float animatedRadius = mix(3.0, 8.0, 0.5 * (1.0 + sin(slowTime * 0.1)));

    // Generate spiral pattern
    float spiral = fract(radius * animatedRadius - slowTime * 5.0 + angle * 5.0);

    // Create a color pattern
    vec3 color = 0.5 + 0.5 * cos(6.28318 * vec3(0.0, 0.33, 0.67) + angle + slowTime);

    // Apply spiral modulation
    color *= step(spiral, 0.5);

    // Output color
    fragColor = vec4(color, 1.0);
}
