float snowflake(vec2 uv, float time) {
   float scale = 10.5;
   float speed = 10.1;
   float amplitude = 0.5;
   float frequency = 1.0;
   float seed = 123.456;
   float value = 0.0;
   for (int i = 0; i < 4; ++i) {
       float n = sin((uv.x * scale + time * speed) * frequency + seed);
       value += n * amplitude;
       scale *= 2.0;
       amplitude /= 2.0;
   }
   return value;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
   float t = iTime;
   vec2 uv = fragCoord / iResolution.xy;
   float flake = snowflake(uv, t);
   float animation = (sin(t) + 1.0) / 2.0;
   //if (uv.y > animation) discard;
   fragColor = vec4(vec3(flake), 1.0);
}
