#define e iResolution.xy
#define j iTime * .2
#define z min( e.x, e.y )

float o(vec2 a) { return dot(a, a); }
mat2 f(float a) { return mat2(cos(a + vec4(0, 11, 33, 0))); }
float h(float a, float b, float c) { return smoothstep(a, b, c); }
float i(inout vec2 b, float d) {
  float a = 6.28 / d, g = (atan(b.y, b.x) + a * .5) / a, c = floor(g),
        k = a * c;
  b *= f(k);
  return c;
}
float l(inout vec2 b, float g) {
  float a = 6.28 / g, k = (atan(b.y, b.x) + a * .5) / a, c = floor(k),
        d = a * c;
  d = c > 5. ? 0. : d, b *= f(d);
  return c;
}
vec2 m(inout vec2 b, vec2 a) {
  vec2 c = a * .5, d = floor((b + c) / a);
  b = mod(b + c, a) - c;
  return d;
}
vec2 p(vec2 a) {
  a.x += cos(j) * .52, a.y += sin(j) * .52;
  return a;
}
vec2 q(inout vec2 a) {
  vec2 b = m(a, vec2(.8, .45)), c = p(b);
  a *= f(atan(c.y, c.x));
  return a;
}
float n(vec2 a, float b) {
  a *= f(1.57);
  float c = i(a, 6.);
  a.x -= b, a *= f(1.57);
  return max(abs(a.x) - .8 * (pow(abs(a.y - .5), 5.) * sqrt(3.)),
             abs(a.y) - .1);
}
float r(vec2 a) {
  vec2 b = a - vec2(.4, .22);
  a = q(a), b = q(b);
  return min(n(a, .09), n(b, .09));
}
float t(vec2 a) {
  a *= f(j), i(a, 8.), a.x -= .35;
  return n(a, .09);
}
float u(vec2 a) {
  m(a, vec2(.05, .06));
  return abs(o(a));
}
float v(vec2 a) {
  a *= f(j * -.1), a *= f(.448571);
  float b = i(a, 5.);
  a.x -= mod(b, 2.) * .1, a.x -= .5, a *= f(.785);
  float c = l(a, 25.);
  a.x -= mod(c, 2.093333) * .1, a *= f(4.71);
  return abs(max(abs(a.x) - .1 * abs(pow(abs(a.y - .5), 3.)), abs(a.y) - .3));
}
float w(vec2 a) {
  a *= f( 3.14 );
  a *= f(j * -.1), a *= f(.448571);
  float b = i(a, 5.);
  a.x -= mod(b, 2.) * .1, a.x -= 1., a *= f(.785);
  float c = l(a, 25.);
  a.x -= .2, a.x -= mod(c, 2.093333) * .1;
  return abs(max(abs(a.x) - .1 * abs(pow(abs(a.y - .5), 3.)), abs(a.y) - .3));
}
float s(vec2 a, vec2 b) { return max(abs(a.x) - b.x, abs(a.y) - b.y); }
float x(vec2 a) {
  bool b = e.x > e.y;
  b ? (a.x = abs(a.x) - (e.x / e.y - .2), a.y = abs(a.y) - .8)
    : (a.x = abs(a.x) - .8, a.y = abs(a.y) - (e.y / e.x - .2));
  return abs(min(s(a + vec2(.18, 0), vec2(.2, .05)),
                 s(a + vec2(0, .18), vec2(.05, .2))));
}
vec3 Z( vec2 c ){
  vec3 a, d = vec3(1);

  a = mix(a, d * .15, h(.1 / z, 0., u(c)) * (1. - step(r(c), 0.))),
  a = mix(a, d * .2, h(4. / z, 0., abs(r(c)))),
  a = mix(a, d * .4, h(5. / z, 0., abs(v(c)))),
  a = mix(a, d * .3, h(5. / z, 0., abs(w(c)))),
  a = mix(a, d * .8, h(5. / z, 0., abs(t(c)))),
  a = mix(a, d, h(.1 / z, 0., o(c))),
  a += step( x(c), 20. / z ) * .2;

  return a;
}
void mainImage( out vec4 U, in vec2 V) {
  U = vec4( 0 );
  int I;
  for( ; I < 2; I++ )
      for( int J = 0; J < 2; J++ )
      U.rgb += Z( ( ( V + vec2( I, J ) ) * 2. - e.xy) / z ) * .25;

}
