void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    vec2 offset = uv * 2.f - 1.f;
    offset *= length(offset);
    offset *= .02f;


    float r = texture(iChannel0, uv).x;
    float g = texture(iChannel0, uv - offset).y;
    float b = texture(iChannel0, uv - offset * 2.f).z;
    fragColor = vec4(r, g, b, 1.f);

}
