    //kolory[4] = vec3(246./255., 46./255., 151./255.);
    //kolory[5] = vec3(21./255., 60./255., 180./255.);

    kolory[0] = vec3(48./255., 3./255., 80./255.);
    kolory[1] = vec3(148./255., 22./255.,127./255.);
    kolory[2] = vec3(233./255., 52./255., 121./255.);
