    punkty[i] = punkty[i]+vec2(0.87*cos(t*0.2),0.4*sin(t*0.3));
    }
    if(mod(float(i),2.)==0.){
    punkty[i] = punkty[i]+vec2(0.6*cos(t*0.25),-0.2*sin(t*0.9));
    }
    float kp = length(uv-punkty[i]);
    float kpp = 1.-step(0.007,kp);
    float d = dystans(uv,punkty[i]);
    if (d<md){
    md=d;
    koli = mod(float(i),6.);
    fkol = kolory[int(koli)];
    }

    }

    vec3 col = fkol;
    // Output to screen
    fragColor = vec4(fkol,1.0);
}
