vec3 palette ( float t) {
    vec3 a=vec3(.5,.5,.5);
    vec3 b=vec3(.5,.5,.5);
    vec3 c=vec3(1.,1.,1.);
    vec3 d=vec3(0.,.33,.67);

    return a+b*cos(6.28318*(c*t+d));
}


float oBox(vec2 p, vec2 a, vec2 b, float th ){
    float l = length(b-a);
    vec2  d = (b-a)/l;
    vec2  q = p-(a+b)*0.5;
          q = mat2(d.x,-d.y,d.y,d.x)*q;
          q = abs(q)-vec2(l*0.5,th);
    return length(max(q,0.0)) + min(max(q.x,q.y),0.0);    
}

float circle( vec2 p, float r ){
    p = vec2(p.x * 1.3, p.y );
    return length(p) - r;
}

float oLines(vec2 p, float w,float r ){
    p = vec2(p.x, abs(p.y));
    return length(p-min(p.x+p.y,w)*.5) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
	float one = min(oBox( p, vec2(-1.1,-.6), vec2(-1.1,.6), .1 ), oBox( p, vec2(-1.45,.25), vec2(-1.16,.55), .07 ));
    float zero = max(circle(p + vec2(.25, 0.0), .6), -circle(p + vec2(.25, 0.0), .35));
    float k = min(oBox( p, vec2(.9,-.6), vec2(.9,.6), .1 ), oLines( p - vec2(1., 0.0), 1.,.1 )) ;


    float shape = min(min(one, zero), k); 
    shape = 1. - shape;
    float cShape = pow(shape, 5.);

    vec3 col = vec3(1.) * step(.8, cShape) + vec3(.5) * cShape + vec3(cShape) * palette(iTime + p.x) ;
	fragColor = vec4(col, 1.0);
}
