void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec3 white = vec3(1, 1, 1);
    vec2 circCenter = vec2(0, 0);
    float circWidth = 0.08;
    float circSpeed = 0.05;

    // Center the coordinates
    vec2 uv = fragCoord - (iResolution.xy / 2.0);
    // Normalized pixel coordinates (from 0 to 1) with respect to the y axis
    uv = uv / iResolution.y;

    float distCenter = distance(circCenter, uv);

    float circRadius = iTime * circSpeed;

    float isInCircle = float(distCenter >= circRadius && distCenter < circRadius + circWidth);

    vec3 col = white * isInCircle;

    // Output to screen
    fragColor = vec4(col, 1.0);
}
