#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define Main void mainImage(out vec4 Q, in vec2 U)
#define pi 3.14159265359
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
vec4 hash44(vec4 p4)
{
	p4 = fract(p4  * vec4(.1031, .1030, .0973, .1099));
    p4 += dot(p4, p4.wzxy+33.33);
    return fract((p4.xxyz+p4.yzzw)*p4.zywx);
}

float map (vec2 U) {
    U *= ei(1.);
    float k = dot(U,U);
    U /= k;
    U.x += 1.;
    vec2 c = vec2(0);
    for (float i = 0.; i < 8.; i++) {
        float o = .25*dot(U,U);
        U /= o;
        k *= o;
        U.y = fract(U.y*.5+.5)*2.-1.;
        U.xy *= mat2(0,-1,1,0);
    }
    return .75*k*length(U)-.005;
}
vec2 grad (vec2 U) {
    vec2 e = vec2(1e-4,0);
    return normalize(vec2(
        map(U+e.xy)-map(U-e.xy),
        map(U+e.yx)-map(U-e.yx)
    ));
}
Main {

    Q = A(U);
    float a = 2.*pi*hash44(vec4(U,iTime,iFrame)).x;
    U = 2.2*(U-.5*R)/R.y;
    U.xy *= ei(-.33);
    vec2 d = vec2(cos(a),sin(a));
    float m = 0.;
    vec2 ex = U;
    for (float i = 0.; i < 80.; i++) {

        m = map(U);

        if (m < 1e-2) {
            d = normalize(d+.125*grad(U));
            ex = U;
        } 

        U += d*max(m,1e-2);

        if (m > 10.) break;

    }
    a = atan(d.x,d.y);
    if (m > 10.) {
        Q += 2.*max(sin(-2.+a+vec4(1,2,3,4)),0.);
        Q += vec4(1,.5,.3,1)*10.*exp(-abs(-100.*ex.y));
        Q += vec4(.3,.5,1,1)*10.*exp(-abs(-100.*(ex.y-.3)));
        Q += vec4(.3,1,.7,1)*10.*exp(-abs(-100.*(ex.y+.3)));
    }
}
