float random (vec2 st) {
    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))* 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 uv = fragCoord/iResolution.y;
  uv = (uv-vec2(5.0))*(abs(sin(iTime*0.1)));
  uv.xy += iTime*0.1;

  vec2 tilingResolution = vec2(6.0, 9.0);

  vec2 blockCoordinates = fract(uv * tilingResolution);
  vec2 tileCoordinates = floor(uv * tilingResolution);

  vec2 randomizedBlockCenter = vec2(mix(0.1, 0.9, random(tileCoordinates / tilingResolution)));

  float distanceFromCenter = distance(randomizedBlockCenter, blockCoordinates);
  float centreFactor = 1.0 - distanceFromCenter;

  float fragmentColor = pow(centreFactor, 2.0);

  // Output to screen
  fragColor = vec4(vec3(fragmentColor), 3);
}
