#define POINT_FACTOR 0.05
#define LINE_WIDTH 0.01


float pointy (float f) {
    return POINT_FACTOR/(abs(f)+POINT_FACTOR);
}

float vMag (vec4 v) {
    return v.x*v.x + v.y*v.y + v.z*v.z;
}

float cubicInOut(float t) {
  return t < 0.5
    ? 4.0 * t * t * t
    : 0.5 * pow(2.0 * t - 2.0, 3.0) + 1.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 texF = texture(iChannel0, uv ).xyz; 
    vec3 texT = texture(iChannel1, uv ).xyz; 

    vec3 col = vec3(0.0);    
    float r = fract(iTime*0.3);    
    float cp = cubicInOut(r);

    float f = abs(uv.x - cp);
    f = step(LINE_WIDTH, f) * (f - LINE_WIDTH);

    col = vec3(pointy(f));
    vec3 bg = mix(texT, texF, step(cp, uv.x));
    //col = bg + col;
    col = mix(bg, col, col.r);
    fragColor = vec4(col, 0);
}
