#define R(a) mix(q*dot(p,q), p, cos(a)) + sin(a)*cross(p,q)

void mainImage( out vec4 o, in vec2 FC )
{
float i,j,d=.1,e,h; vec3 p,q=vec3(-1);
o=vec4(0.);

for(i=0.;i++<64.&&d>1e-4;){
    p=vec3((FC.xy-.5*iResolution.xy)/iResolution.y,e)*64.;
    p.z-=12.;
    p=R(iTime*.3);

    for(j=0.;j++<4.;)
        p=abs(abs(cross(mod(p,12.),vec3(-1)))-.03)-.04,
        d=dot(length(p.xy),p.z)/1e4;

    e+=d;
}
o+=vec4(sqrt(p),1)*5./i;
}
