vec2 tc = vec2(1.,0.);

// cheap gradient
vec3 grad(float x)
{
    return mix(mix(tc.yyx, tc.xxy, x/.5), tc.xyy, (x - .5)/(1.0 - .5) ) ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    // sample texture
    vec3 c = texture(iChannel0,uv).rgb;

    // cheap luma approximation
    float l = (c.r + c.g + c.b) /3.0;

    // luma -> color grad
    vec4 col = vec4( grad(l), 1.0);

    fragColor = col;
}
