float sdCircle( vec2 p, float r )
{
    return length(p) - r;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y * 8.0;

    float y = cos(uv.x + iTime) - 2.0;

    vec3 col;

    vec2 sun_origin = vec2(uv.x + 12.0, uv.y - 6.0);

    vec3 yellow = vec3(1.0,1.000,0.000);

    vec3 black = vec3(0.0,0.0,0.0);

    if(uv.y < y){
        col = vec3(0.059,0.808,0.859);
    }else{
        col = vec3(0.227,0.376,0.514);
    }

    float sun_dist = sdCircle(sun_origin, 5.0);

    col = sun_dist > 0.0 ? col + black : yellow;

    // Output to screen
    fragColor = vec4(col,1.0);
}
