// OG version 839 chars
float draweye(vec2 pos, vec2 uv, float r, float blur)
{
    float d = length(uv-pos);
    return smoothstep(abs(r), r-blur, d);
}
float drawpupil(vec2 pos, vec2 uv, float r, float blur)
{
    float d = length(uv-pos);
    d = sin(d * ((iTime*0.000000001)+40.));
    d = abs(d);
    return smoothstep(abs(r), r-blur, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2. - iResolution.xy)/ iResolution.y;
    uv.x = abs(uv.x);

    float rightPupil = drawpupil(vec2(0.57, 0.05), vec2(uv), 0.4, 0.3);
    if (uv.x<0.) rightPupil = 0.0;

    float rightEye = draweye(vec2(0.5, 0.42), vec2(uv), 0.6, 0.01) * draweye(vec2(0.7, -0.3), vec2(uv), 0.6, 0.01);

    vec3 col = vec3(0.9, 0.8, 1.0) * (rightEye-rightPupil);
    fragColor = vec4(col, 1.0);
}
