void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord / iResolution.xy;
    vec4 color = texture(iChannel0, uv);

    float r = color.r;
    float g = color.g;
    float b = color.b;

    float sepiaR = (r * 0.393 + g * 0.769 + b * 0.189) * 0.9;
    float sepiaG = (r * 0.349 + g * 0.686 + b * 0.168) * 0.85;
    float sepiaB = (r * 0.272 + g * 0.534 + b * 0.131) * 0.8;

    fragColor = vec4(sepiaR, sepiaG, sepiaB, color.a);
}
