// Shadertoy shader script
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x *= iResolution.x / iResolution.y;

    float time = iTime * 0.5;
    float angle = atan(uv.y, uv.x);
    float radius = length(uv);

    vec3 color = vec3(0.0);
    float speed = 0.5 + 0.5 * sin(time);
    float wave = sin(10.0 * radius - time * 5.0);
    float pattern = cos(10.0 * angle + wave * 5.0);

    color.r = 0.5 + 0.5 * sin(time + pattern * 3.0);
    color.g = 0.5 + 0.5 * cos(time + pattern * 3.0);
    color.b = 0.5 + 0.5 * sin(time + pattern * 3.0 + 1.57);

    fragColor = vec4(color, 1.0);
}
