//I think I'm getting the hang of what shaders can do at least in a really limited sense.


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    float a = fract((uv.x-.5)*iTime)-.5;
    float b = fract((uv.y-.5)*iTime)-.5;
    float c = fract(sin(a*b)*18.0+18.0);

    // Output to screen
    fragColor = vec4(a+c, b/c/a, b+c, 1.0);
}
