float signal(vec2 p, float s, float w) {
    vec2 d = fwidth(p) * (w / s);
    vec2 x = abs(fract(p / s - 0.5) - 0.5);
    vec2 x1 = 1.0 - x / d;
    vec2 x2 = max(x1, 0.0);
    return x2.x + x2.y;
}

float fade(vec2 pos, float minRad, float maxRad) {
    float d = length(pos - iMouse.xy);
    float f = smoothstep(minRad, maxRad, d);
    return 1.0 - f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy / 20.0;
    float w = fade(fragCoord, 100.0, 600.0) * 2.0;
    float f = signal(p, 1.0, w);

    float w2 = fade(fragCoord, 100.0, 800.0) * 2.0;
    float f2 = signal(p, 10.0, w2);

    vec3 col = vec3(0.8);
    col.xyz *= max(f * 0.3, f2);

    fragColor = vec4(col,1.0);
}
