// Function to create a 'pinwheel' with adjustable spin, colors, wing length, radius, and ripple effect
vec3 createPinwheel(vec2 uv, float radius, vec3 color1, vec3 color2, float wingLength, float spin, float ripple) {
    float radialDist = length(uv) * 2.0;
    radialDist = radialDist / radius;

    // Ripple effect
    radialDist += sin(radialDist * 20.0 + ripple * 2.0 * 3.14159) * 0.05;

    float angle = atan(uv.y, uv.x) + spin * 2.0 * 3.14159;

    vec3 rainbow = mix(color1, color2, (cos(angle * 6.0 + radialDist * wingLength) + 1.0) / 2.0);
    rainbow *= smoothstep(1.0, 0.3, radialDist);

    return rainbow;
}

// Global variable to accumulate spin over time
float spin = 0.0;

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / min(iResolution.y, iResolution.x);

    vec3 baseColor = vec3(0.1, 0.2, 0.3); // Adjust these values for different base colors
    float seed = 1.61803398875; // Golden ratio for interesting fractal patterns

    vec3 finalColor = vec3(0.0);

    // Define properties and movement for 10 pinwheels
    for (int i = 0; i < 1; i++) {
        float t = iTime * 0.1 + float(i);
        float x = 0.0;
        float y = float(i) / 10.0;
        float radius = 1.0;
        vec3 color1 = vec3(0.3, 0.1, 1.0);
        vec3 color2 = vec3(0.0, 0.9, 0.1);
        float wingLength = 5.0;

        // Update spin based on mouse click
        if (iMouse.z > 0.0) {
            spin += 0.1; // Increment spin on mouse click
        }

        float ripple = cos(spin);

        vec2 pinwheelPos = uv - vec2(x, y);
        vec3 pinwheelColor = createPinwheel(pinwheelPos, radius, color1, color2, wingLength, spin, ripple);
        finalColor = max(finalColor, pinwheelColor);
    }

    fragColor = vec4(finalColor, 1.0);
}
