float Circle(vec2 uv, vec2 p, float r, float blur) {
    float d = length(uv - p);
    float c = smoothstep(r, r-blur, d);
    return c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;
    vec3 col = vec3(0.);
    float mask = Circle(uv, vec2(0.775, -0.385), .1, .01);
    mask -= Circle(uv, vec2(0.81, -0.365), .025, .01);
    mask -= Circle(uv, vec2(0.74, -0.365), .025, .01);
    mask += Circle(uv, vec2(-0.775, -0.385), .1, .01);
    mask -= Circle(uv, vec2(-0.81, -0.365), .025, .01);
    mask -= Circle(uv, vec2(-0.74, -0.365), .025, .01);
    mask += Circle(uv, vec2(-0.775, 0.385), .1, .01);
    mask -= Circle(uv, vec2(-0.81, 0.365), .025, .01);
    mask -= Circle(uv, vec2(-0.74, 0.365), .025, .01);
    mask += Circle(uv, vec2(0.775, 0.385), .1, .01);
    mask -= Circle(uv, vec2(0.81, 0.365), .025, .01);
    mask -= Circle(uv, vec2(0.74, 0.365), .025, .01);
    float mouth = Circle(uv, vec2(0.775, -0.385), .075, .01);
    mouth -= Circle(uv, vec2(0.775, -0.360), .075, .01);
    mouth += Circle(uv, vec2(-0.775, -0.385), .075, .01);
    mouth -= Circle(uv, vec2(-0.775, -0.360), .075, .01);
    mouth += Circle(uv, vec2(-0.775, 0.385), .075, .01);
    mouth -= Circle(uv, vec2(-0.775, 0.360), .075, .01);
    mouth += Circle(uv, vec2(0.775, 0.385), .075, .01);
    mouth -= Circle(uv, vec2(0.775, 0.360), .075, .01);
    mask -= mouth;
    col = vec3(1., 1., 0.)*mask;
    fragColor = vec4(col, 1.0);
}
