void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 3.1415*(fragCoord*2.-iResolution.xy)/iResolution.y;
    vec2 ro = vec2(uv.x,0);
    vec2 rd = vec2(0,1);
    float d = 1.;
    for (int i=0;i<64;i++) {
        if ((d>0.&&ro.y>sin(ro.x))||(d<0.&&ro.y<sin(ro.x)))
            d = -d/2.;
        ro += rd*d;
        if (abs(d)<.001) {
            if (distance(ro,uv)<.01) {
                fragColor = vec4(1);
                return;
            }
            break;
        }
    }
    fragColor = vec4(0);
}
