#define pi 3.1415926535
#define rate 10.
float max_iters = 80.;

vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

vec4 map_color(float i, float r, float c) {
    float di = i;
    float zn = sqrt(r + c);
    float hue = (di + 1. - log(log(abs(zn))))/max_iters+0.3;
    return vec4(hsl2rgb(vec3(1.-hue, 1, 0.5)),1);
}

void mainImage( out vec4 color, in vec2 fragmentCoord )
{
    float aspect = iResolution.y/iResolution.x;
    vec3 pointCoord = vec3(fragmentCoord/iResolution.xy*3.-1.5, 1.);
    vec2 c = 0.7885*vec2(sin(iTime*pi/rate), cos(iTime*pi/rate));
    float zx = pointCoord.x/aspect;
    float zy = pointCoord.y;
    float iter = 0.;
    while (iter < max_iters) {
        float nzx = zx * zx - zy * zy +c.x;
        float nzy = 2. * zx * zy + c.y;
        zx = nzx;
        zy = nzy;
        if (zx*zx + zy*zy  > 4.) {
            break;
        }
        iter += 1.;
    }
    if (iter == max_iters) {
        color = vec4(0,0,0,1);
    } else {
        color = map_color(iter, zx*zx, zy*zy);
    }
}
