// Midtone Adjustment Shader

const float MIDTONES = 0.0;
const vec3 MIDTONES_RGB = vec3(1.0, 1.0, 1.0);

vec3 adjustMidtones(vec3 color, float adjustment, vec3 midtoneAdjustRGB) {
    float luminance = dot(color, vec3(0.299, 0.587, 0.114));
    float factor = smoothstep(0.3, 0.7, luminance);
    float gamma = 1.0 + adjustment * 0.2;
    vec3 adjustedColor = pow(color, vec3(gamma)) * midtoneAdjustRGB;
    return mix(color, adjustedColor, factor);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.xy;

    vec3 color = texture(iChannel0, uv).rgb;

    color = adjustMidtones(color, MIDTONES, MIDTONES_RGB);

    color = clamp(color, 0.0, 1.0);

    fragColor = vec4(color, 1.0);
}
