// using "bicubic interpolation 2"  https://shadertoy.com/view/MfsBDB
// see also this mat4 filter variant: https://www.shadertoy.com/view/XcffWj

void mainImage( out vec4 O, vec2 u )
{
    O = texelFetch(iChannel0,ivec2(u), 0);
}

//#define interp nearest
//#define interp bilin
  #define interp bicubic

#define nearest(P,c)  texelFetch(iChannel0, ivec2(P), 0 )[c]
#define bilin(P,c)    texture(iChannel0,P/iResolution.xy)[c]

// field values and derivatives
#define f(x,y)    texelFetch(iChannel0, ivec2(I + vec2(x,y)), 0 )[c]
#define fx(x,y)  ( f(x+1,y) - f(x-1,y) ) / 2.
#define fy(x,y)  ( f(x,y+1) - f(x,y-1) ) / 2.
#define fxy(x,y) ( f(x+1,y+1) - f(x-1,y+1) - f(x+1,y-1) + f(x-1,y-1) ) / 4.

float bicubic(vec2 P, int c) // https://en.wikipedia.org/wiki/Bicubic_interpolation  
{
   P -= .5;
   vec2 I = floor(P),
        F = fract(P);
   mat2x4 B = transpose( mat4x2(1,1,F,F*F,F*F*F) );     
    mat4 A =   mat4( 1,0,-3,2, 0,0,3,-2, 0,1,-2,1, 0,0,-1,1 )
             * mat4( f (0,0), f (1,0), fx (0,0), fx (1,0),
                     f (0,1), f (1,1), fx (0,1), fx (1,1),
                     fy(0,0), fy(1,0), fxy(0,0), fxy(1,0),
                     fy(0,1), fy(1,1), fxy(0,1), fxy(1,1) )
             * mat4( 1,0,0,0, 0,0,1,0, -3,3,-2,-1, 2,-2,1,1);
   return dot( B[0] * A , B[1] );  // (1,x,x*x,x*x*x) * A  * (1,y,y*y,y*y*y) 
}          

void mainImage( out vec4 O, vec2 u )
{
    float a = radians(1.);
    vec2 R = iResolution.xy, U =  u - R/2.;                                   // centering
    O = iFrame < 1
      ?    U /= R, 
        ( .6 + .6 * cos( 6.3*U.x  + vec4(0,23,21,0)  )  ) *step(abs(U.y),.01) // hue bar
      : ( U = R/2. + U * mat2( cos(a),-sin(a),sin(a),cos(a) ),                // rotation 1°
         vec4( interp(U,0), interp(U,1), interp(U,2), 1) );
}
