#define PI 3.14159265359

mat2 scale2D(vec2 _scale){
    return mat2(_scale.x,0.0,
                0.0,_scale.y);
}

mat2 rot2D(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return mat2(c, -s, s, c);
}

float box(in vec2 _st, in vec2 _size){
    _size = vec2(0.5) - _size*0.5;
    vec2 uv = smoothstep(_size,
                        _size+vec2(0.001),
                        _st);
    uv *= smoothstep(_size,
                    _size+vec2(0.001),
                    vec2(1.000,1.000)-_st);
    return uv.x*uv.y;
}

float cus_cross(in vec2 _st, float _size){
    return  box(_st, vec2(_size,_size/4.)) +
            box(_st, vec2(_size/4.,_size));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    //uv *= 2.0;

    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.0);

    // Rotation
    uv = rot2D( sin(iTime)*PI ) * uv;
    uv = scale2D( vec2(sin(iTime)+1.0) ) * uv;
    uv += 0.5;

    // To move the cross we move the space
    vec2 translate = vec2(cos(iTime),sin(iTime));
    //uv += translate*0.35;

    // Add the shape on the foreground
    col += vec3(cus_cross(uv,0.25));
    //col = vec3(uv.x, uv.y, 0.0);

    fragColor = vec4(col,1.0);
}
