const int SEGMENT_LENGTH = 13;
const ivec2 idims = ivec2(320, 180);
const vec2 dims = vec2(idims);

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;
    int globalind = int(uv.x * dims.x) + int(uv.y * dims.y) * idims[0];

    float error = 0.0;
    for (int i = globalind / SEGMENT_LENGTH * SEGMENT_LENGTH; i <= globalind; i++) {
        error -= float(error > 0.5);

        vec2 pos = vec2(i % idims[0], i / idims[0]);
        float lightness = length(texture(iChannel0, pos / dims).rgb) / sqrt(3.0);
        error += pow(lightness, 1.0);
    }

    fragColor = vec4(error > 0.5);
}
