void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    float t = (sin(iTime * 1.4 - uv.x)*.5+.5);
    float c = mix(.05, .25, t);

    vec2 a = vec2(abs(mod(uv.x,1./10.)-.04), abs(mod(uv.y,1./10.)-.04));

    float isLineV = float(a.x < .0025);
    float isLineH = float(a.y < .0025);
    c += isLineV *.4 * t;
    c += isLineH *.4 * t;

    float isDot = float(a.x + a.y < .0025);
    c += isDot * mix(.2, .8, t);

    fragColor = vec4(vec3(c), 1.);

}
