#define TOTAL 100.
vec2 N22(vec2 p){
    vec3 a = fract(p.xyx*vec3(863.245, 193.35, 219.731));
    a += dot(a, a+71.47);
    return fract(vec2(a.x*a.y, a.y*a.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.xy;
    vec3 col = vec3(0);

    float minD = 100.;
    float cid = 0.;

    float m = 0.;     
    float t = iTime*.3+100.; 

    for(float i = 0.; i < TOTAL; i++){
        vec2 n = N22(vec2(i*64.25, i*19.99))+cos(uv.x+t)*.05;
        vec2 p = cos(n*t)*.35;
        float d = length(uv-p);

        m += smoothstep(.02, .01, d);

        if(d<minD){
            minD = d;
            cid = i;
        }

    }

    vec3 base = sin(t*vec3(5., 4., 6.)+2.)*.4+.8;
    col -= minD*base*3.;
    col += cid/TOTAL;
    //col = vec3(N22(uv).y);
    fragColor = vec4(col,1.0);
}
