#define R(p,a,r)mix(a*dot(p,a),p,cos(r))+sin(r)*cross(p,a)
#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
mat2 rot(float a) {return mat2(cos(a),sin(a),-sin(a),cos(a));}
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
        vec2 uv = (C * 2.0 - iResolution.xy) / iResolution.y;

    vec3 p,q,r=iResolution,
    d=normalize(vec3((C*2.-r.xy)/r.y,1));  
    for(float i=0.,a,s,e,g=0.;
        ++i<70.;
        O.xyz+=mix(vec3(1),H(g*.1),.8)*10./e/8e3
    )
    {
        p=g*d;
      float scale = 0.6;   //7.//5.41//3.5;//2.545;//1.91;//1.2726;//.6365;
float t = iTime;

        uv+= vec2(log(length(uv)), atan(uv.y, uv.x))*scale;



        p.xy-=uv;
        a=20.;
        p=mod(p-a,a*2.)-a;
        s=3.;
        p.xz*=rot(iTime);

             p.yz*=rot(iTime);
        for(int i=0;i++<8;){

            p=.8-abs(p);
            p.x<p.z?p=p.zyx:p;
            p.z<p.y?p=p.xzy:p;
            s*=e=1.7+sin(iTime*.2)*.1;
            p=abs(p)*e-
                vec3(
                    5.*3.,
                    120,
                    8.*5.
                 );
         }
         g+=e=length(p.yz)/s;
    }
}
